mapper.update:
update tb_recipe
   set viewcnt = viewcnt + 1
 where recipe_no = #{recipeNo}

mapper.select:
		select count(*) 
  		  from tb_recipe_follow 
         where follow_mem_no = #{followMemNo}
           and follower_mem_no = #{followerMemNo}

		select follow_status
		  from tb_recipe_follow
		 where follow_mem_no = #{followMemNo}
           and follower_mem_no = #{followerMemNo}

mapper.update:
		update tb_recipe_follow
		   set follow_status = #{followStatus}
		 where follow_mem_no = #{followMemNo}
           and follower_mem_no = #{followerMemNo}

mapper.select:
		select count(*) 
  		  from tb_recipe_like
         where recipe_no = #{recipeNo}
           and mem_no = #{memNo}

mapper.update:
update tb_recipe
   set likecnt = likecnt + 1
 where recipe_no = #{recipeNo}

update tb_recipe
   set likecnt = likecnt - 1
 where recipe_no = #{recipeNo}

		update tb_recipe_like
		   set like_status = #{likeStatus}
		 where recipe_no = #{recipeNo}
           and mem_no = #{memNo}

mapper.select:
select like_status
  from tb_recipe_like
 where recipe_no = #{recipeNo}
   and mem_no = #{memNo}

select likecnt
  from tb_recipe
 where recipe_no = #{recipeNo}

mapper.update:
update tb_recipe
   set title = #{title},
       content = #{content}
 where recipe_no = #{recipeNo}

mapper.insert:

    	insert into tb_recipe_review (
			recipe_no, content, mem_no, score    	
    	) values (
    		#{recipeNo}, #{content}, #{memNo}, #{score}
     	)

    	SELECT LAST_INSERT_ID()

mapper.update:
update tb_recipe_review
   set content = #{content}    	         
 where recipe_review_no = #{recipeReviewNo}

mapper.select:
select r.*,m.mem_nickname
  from tb_recipe r,tb_recipe_keyword k,tb_member m
 where m.mem_no = r.mem_no
   and r.recipe_no = k.recipe_no
   and k.country = #{no} 
       or k.situation = #{no} 
       or k.level = #{no} 
       or k.type = #{no}
 order by r.recipe_no desc
